import React, { useState, useEffect } from 'react';

const Accordion = () => {
    const lists = [];
    const [list, setList] = useState([]);

    const fetchTodos = () => fetch('https://jsonplaceholder.typicode.com/todos').then(response => response.json()).then(setList);


    useEffect(async () => {
        const todos = await fetchTodos();
        setList(todos);
    }, []);

    return (
        <ul>
            {lists.map((list, idx) => <li key={idx}>{list.title}</li>)}
        </ul>
    )
}

export { Accordion }
